$(".like-btn").click(function(e){e.preventDefault();var s=$(this).val();$.ajax({url:"news/LikeNewsItem",type:"POST",cache:!1,data:{newsid:s},success:function(e){e.success&&$("#scoreid-"+s).text(e.message)},error:function(e){alert("error")}})}),$(".dislike-btn").click(function(e){e.preventDefault();var s=$(this).val();$.ajax({url:"news/DislikeNewsItem",type:"POST",cache:!1,data:{newsid:s},success:function(e){e.success&&$("#scoreid-"+s).text(e.message)},error:function(e){alert("error")}})});