$(function(){var e=window.location.pathname,t=e.substring(e.lastIndexOf("/")+1);switch(t){case"all":$(".sm-container a#allnews").addClass("active-option"),$(".sm-container a#popular").removeClass("active-option");break;case"popular":$(".sm-container a#popular").addClass("active-option"),$(".sm-container a#allnews").removeClass("active-option")}}),$(".like-btn").click(function(e){e.preventDefault();var t=window.location,a=t.protocol+"//"+t.host+"/"+t.pathname.split("/")[0],i=$(this).val();$.ajax({url:a+"/news/LikeNewsItem",type:"POST",cache:!1,data:{newsid:i},success:function(e){e.success&&$("#scoreid-"+i).text(e.message)},error:function(e){alert("error")}})}),$(".dislike-btn").click(function(e){e.preventDefault();var t=window.location,a=t.protocol+"//"+t.host+"/"+t.pathname.split("/")[0],i=$(this).val();$.ajax({url:a+"news/DislikeNewsItem",type:"POST",cache:!1,data:{newsid:i},success:function(e){e.success&&$("#scoreid-"+i).text(e.message)},error:function(e){alert("error")}})}),$(".view-tracker").click(function(){var e=$(this).attr("id");$.ajax({url:"news/AddViewToNewsItem",type:"POST",cache:!1,data:{newsid:e}})}),$(".view-related").click(function(e){e.preventDefault(),$(this).closest("div").find(".related-articles").slideToggle(200),$(this).text("View Related Articles"===this.text?"Hide Related Articles":"View Related Articles")});